// This file will be generated from js/main.js in a real build process
document.addEventListener('DOMContentLoaded', function() {
  // Include the main.js content
  const script = document.createElement('script');
  script.src = '../js/main.js';
  document.body.appendChild(script);
  
  // Include Typed.js for typing animation if used
  const typedScript = document.createElement('script');
  typedScript.src = 'https://cdn.jsdelivr.net/npm/typed.js@2.0.12';
  document.body.appendChild(typedScript);
});
